import React, { useState } from "react";
import TutorialContent from "./TutorialContent";

interface ITutorialContent {
  contentId: number;
  title: string;
  content: string;
  imgSrc: string;
}

// type ITutorialContents = Array<ITutorialContent>;

export default function TutorialTap() {
  const [currentSlideIndex, setCurrentSlideIndex] = useState(0);

  const [isOpen, setIsOpen] = useState<Record<string, boolean>>({
    page1: true,
    page2: false,
    page3: false,
  });

  const tutorialContents: Array<ITutorialContent> = [
    {
      contentId: 1,
      title: "1번 제목",
      content: "1번의 내용입니다. 설명을 적어주세요.",
      imgSrc:
        "data:image/jpeg;base64,",
    },
    {
      contentId: 2,
      title: "2번 제목",
      content: "2번의 내용입니다. 설명을 적어주세요.",
      imgSrc:
        "data:image/jpeg;base64,",
    },
    {
      contentId: 3,
      title: "3번 제목",
      content: "3번의 내용입니다. 설명을 적어주세요.",
      imgSrc:
        "data:image/jpeg;base64,",
    },
  ];

  const tabHandler = (e: React.MouseEvent<HTMLElement>) => {
    const target = e.target as HTMLElement;
    const newtarget = target.id as keyof typeof isOpen;
    const newTab: { [key: string]: boolean } = {
      page1: false,
      page2: false,
      page3: false,
    };
    newTab[newtarget] = true;
    setIsOpen(newTab);
  };

  const slideHandler = (e: React.MouseEvent<HTMLElement>) => {
    const target = e.target as HTMLElement;
    const direction = target.id;
  };

  const moveLeft = () => {
    if (currentSlideIndex === 0) {
      setCurrentSlideIndex(tutorialContents.length - 1);
    } else {
      setCurrentSlideIndex(currentSlideIndex - 1);
    }
  };

  const moveRight = () => {
    if (currentSlideIndex === tutorialContents.length - 1) {
      setCurrentSlideIndex(0);
    } else {
      setCurrentSlideIndex(currentSlideIndex + 1);
    }
  };

  return (
    <div>
      <div className="tutorial-tab-buttons-container">
        <button type="button" id="page1" onClick={tabHandler}>
          처음으로
        </button>
        <button
          type="button"
          id="page1"
          onClick={tabHandler}
          className={isOpen.page1 ? "button-active" : ""}
        >
          ① 기본 내용 설정하기
        </button>
        <button
          type="button"
          id="page2"
          onClick={tabHandler}
          className={isOpen.page2 ? "button-active" : ""}
        >
          ② 커스텀 하기
        </button>
        <button
          type="button"
          id="page3"
          onClick={tabHandler}
          className={isOpen.page3 ? "button-active" : ""}
        >
          ③ 시작이랑 종료는?
        </button>
      </div>
      <div className="tutorial-contents-carousel">
        <button type="button" onClick={moveLeft}>
          ◀
        </button>
        <div
          className="tutotial-contents-slide"
          id="slide"
          style={{ transform: `translateX(-${currentSlideIndex * 100}%)` }}
        >
          {tutorialContents.map((item, index) => {
            return (
              <TutorialContent
                contents={item}
                key={item.contentId}
                
              />
            );
          })}
        </div>
        <button type="button" onClick={moveRight}>
          ▶
        </button>
      </div>
    </div>
  );
}
